  let images = {};
  r.keys().map((item) => {
    return (images[item.replace("./", "")] = r(item));
  });
  return images;
};

const Shop = () => {
  const [breads, setBreads] = useState(getBreads());
  const [coffee, setCoffee] = useState(Menu.coffee);

  return (
    <div className="Shop">
      <Header />
      <img src={breads[0].imageURL} />
    </div>
  );
};

export default Shop;
